CREATE TABLE `yeonit`.`yeonit_users` ( `identify` INT(11) NOT NULL AUTO_INCREMENT UNIQUE , `tag` VARCHAR(32) NOT NULL , `name` VARCHAR(64) NOT NULL , `avatar` VARCHAR(256) NOT NULL , `experience` INT(11) NOT NULL , `matchmakingRate` INT(11) NOT NULL DEFAULT '0' , `playedMatches` INT(11) NOT NULL , `createdTime` VARCHAR(32) NOT NULL , `lastSeenTime` VARCHAR(32) NOT NULL , `wonMatches` INT(11) NOT NULL , `defeatMatches` INT(11) NOT NULL , `blocked` TINYINT(1) NOT NULL DEFAULT '0' , `blockedReason` VARCHAR(2048) NOT NULL , `blockedUntil` VARCHAR(32) NOT NULL , `permission` INT(11) NOT NULL , `email` VARCHAR(320) NOT NULL , `status` INT(11) NOT NULL , `locale` VARCHAR(16) NOT NULL , `provider` VARCHAR(32) NOT NULL , `providerIdentify` VARCHAR(32) NOT NULL , PRIMARY KEY (`identify`)) ENGINE = InnoDB;
